            cipher.init(Cipher.PUBLIC_KEY, publicKey);

            byte[] encryptedTokenBytes = cipher.doFinal(token.getBytes(StandardCharsets.UTF_8));

            return new String(BASE64_CODEC.encode(encryptedTokenBytes), StandardCharsets.UTF_8);
