package se.tink.backend.aggregation.agents.nxgen.serviceproviders.openbanking.ukopenbanking.base.configuration;

import com.fasterxml.jackson.annotation.JsonProperty;
import com.kjetland.jackson.jsonSchema.annotations.JsonSchemaDescription;
import com.kjetland.jackson.jsonSchema.annotations.JsonSchemaExamples;
import com.kjetland.jackson.jsonSchema.annotations.JsonSchemaInject;
import com.kjetland.jackson.jsonSchema.annotations.JsonSchemaInt;
import com.kjetland.jackson.jsonSchema.annotations.JsonSchemaString;
import com.kjetland.jackson.jsonSchema.annotations.JsonSchemaTitle;
import java.util.Optional;
import se.tink.backend.aggregation.agents.nxgen.serviceproviders.openbanking.ukopenbanking.base.tls.LocalCertificateTlsConfigurationSetter;
import se.tink.backend.aggregation.agents.nxgen.serviceproviders.openbanking.ukopenbanking.base.tls.TlsConfigurationSetter;
import se.tink.backend.aggregation.annotations.JsonObject;
import se.tink.backend.aggregation.annotations.Secret;
import se.tink.backend.aggregation.annotations.SensitiveSecret;
import se.tink.backend.aggregation.configuration.agents.ClientIdConfiguration;

@JsonObject
public class UkOpenBankingConfiguration implements UkOpenBankingClientConfigurationAdapter {

    @JsonSchemaTitle("Signing key - OB Seal/OB Signing")
    @JsonSchemaDescription("Private key used to sign requests, base64 encoded ")
    @JsonSchemaExamples("MIIEwA [...] QZw=")
    @JsonSchemaInject(ints = {@JsonSchemaInt(path = "minLength", value = 1000)})
    @JsonProperty(required = true)
    @Secret
    private String signingKey;

    /** @deprecated Delete this after migration to use eIDAS */
    @Deprecated
    @JsonSchemaTitle("Signing key id as in UKOB Directory")
    @JsonSchemaDescription("Signing key id as in UKOB Directory")
    @JsonSchemaExamples("ZTBCaTMm-taVUpZKsgF-ClEVoMM")
    @JsonProperty(required = true)
    @Secret
    private String signingKeyId;

    @JsonSchemaTitle("SSA JWT obtained from UKOB Directory")
    @JsonSchemaDescription(
            "SSA can be obtained by going to Directory, choosing appropriate software client and clicking the 'Generate' button.")
    @JsonSchemaExamples(
            "eyJhbGciOiJQUzI1NiIsImtpZCI6Ikh6YTl2NWJnREpjT25oY1VaN0JNd2JTTF80TlYwZ1NGdklqYVNYZEMtMWM9IiwidHlwIjoiSldUIn0..sBFpkUCqMraUfRts2294fYxGc7d_0duTIU4s0I48B9jT8398vI8p9NsrrkexgLAnUbHIurGuGEqk3flM4pMxMVmZtCn4AFQoXDM2r2f0d7d1bbfOJ9MbgPvxi0sOEZlGZGVZogyjKtAHSdWMqDkNWLdPqLRil3PadqttDWv6mIuOFXjjYlkclSW9KgT8vI1RMgMdhLNFPJN18jNZdrZbfNtantcQcbFiOnYvpg8fB6PskOZLM8cMkRCAnBde3XLVAN8JcFz0YCO4gG23c5Kom_ftMlkKGcV3TWJX-o04g_n0k2xC1Cznr-YHQjOHX784tUv_xdoAWA9SVWnlNSl81Q")
    @JsonSchemaInject(ints = {@JsonSchemaInt(path = "minLength", value = 1000)})
    @JsonProperty(required = true)
    @Secret
    private String softwareStatementAssertion;

    @JsonSchemaTitle(
            "Transport key (OB WAC/OB Transport) and certificate in PKCS12 format, base 64 encoded")
    @JsonSchemaDescription(
            "Conversion of key and certificate to the desired format can be done with the following command: '$ openssl pkcs12 -export -inkey obwac.key -in obwac.crt | base64'")
    @JsonSchemaExamples("MIIMa [...] UbZfAgIIAA==")
    @JsonSchemaInject(ints = {@JsonSchemaInt(path = "minLength", value = 1000)})
    @JsonProperty(required = true)
    @Secret
    private String transportKey;

    @JsonSchemaTitle("Token Endpoint Auth Signing algorithm")
    @JsonSchemaDescription(
            "Algorithm which the TPP uses to authenticate with the token endpoint if using private_key_jwt or client_secret_jwt. This must be the same algorithm as the one chosen during registration. Must be specified if token_endpoint_auth_method is private_key_jwt or client_secret_jwt")
    @JsonSchemaInject(strings = {@JsonSchemaString(path = "pattern", value = "^(RS256|PS256)$")})
    @JsonSchemaExamples("PS256")
    @JsonProperty
    @Secret
    private String tokenEndpointAuthSigningAlg;

    @JsonSchemaTitle("Token Endpoint Authentication Method")
    @JsonSchemaDescription(
            "Specifies which Token endpoint authentication method the TPP wants to use. This must be the same method as the one chosen during registration.")
    @JsonSchemaInject(
            strings = {
                @JsonSchemaString(
                        path = "pattern",
                        value =
                                "^(private_key_jwt|client_secret_basic|client_secret_post|tls_client_auth)$")
            })
    @JsonSchemaExamples("tls_client_auth")
    @JsonProperty(required = true)
    @Secret
    private String tokenEndpointAuthMethod;

    @SensitiveSecret @ClientIdConfiguration private String clientId;

    @JsonSchemaTitle("Client secret")
    @JsonSchemaDescription("Client secret obtained during registration. Empty string if N/A.")
    @JsonSchemaExamples("dba7e5eb-4dfc-4fd1-9dc7-4bff9a30b2cf")
    @JsonProperty(required = true)
    @SensitiveSecret
    private String clientSecret;

    @JsonSchemaTitle("Password to the transport keystore")
    @JsonSchemaDescription("Password to the PKCS12 transport key keystore. Empty string if N/A.")
    @JsonSchemaExamples("ThisIsVeryStrongPassword42!")
    @JsonProperty(required = true)
    @SensitiveSecret
    private String transportKeyPassword;

    public String getClientId() {
        return clientId;
    }

    public String getSigningKey() {
        return signingKey;
    }

    /** @deprecated */
    @Deprecated
    public String getSigningKeyId() {
        return signingKeyId;
    }

    public String getTransportKey() {
        return transportKey;
    }

    public String getClientSecret() {
        return clientSecret;
    }

    public String getTransportKeyPassword() {
        return transportKeyPassword;
    }

    @Override
    public ClientInfo getProviderConfiguration() {
        return new ClientInfo(
                clientId, clientSecret, tokenEndpointAuthMethod, tokenEndpointAuthSigningAlg);
    }

    @Override
    public SoftwareStatementAssertion getSoftwareStatementAssertions() {
        return SoftwareStatementAssertion.fromJWT(softwareStatementAssertion);
    }

    @Override
    public Optional<TlsConfigurationSetter> getTlsConfigurationOverride() {
        return Optional.of(
                new LocalCertificateTlsConfigurationSetter(transportKey, transportKeyPassword));
    }
}
