package se.tink.backend.aggregation.agents.nxgen.fr.banks.boursorama;

import static org.assertj.core.api.Assertions.assertThat;

import java.util.HashMap;
import java.util.Map;
import org.junit.Before;
import org.junit.Test;
import se.tink.backend.aggregation.agents.nxgen.fr.banks.boursorama.authenticator.BoursoramaImageMapper;

public class BoursoramaImageMapperTest {
    private BoursoramaImageMapper imageMapper;
    private static final Map<String, String> codeToImageMap = new HashMap<>();

    static {
        codeToImageMap.put(
                "abc",
                "");
        codeToImageMap.put(
                "def",
                "");
        codeToImageMap.put(
                "ghi",
                "");
        codeToImageMap.put(
                "jkl",
                "");
        codeToImageMap.put(
                "mno",
                "");
        codeToImageMap.put(
                "pqr",
                "");
        codeToImageMap.put(
                "stv",
                "");
        codeToImageMap.put(
                "uvw",
                "");
        codeToImageMap.put(
                "xyz",
                "");
        codeToImageMap.put(
                "qwe",
                "");
    }

    @Before
    public void setup() {
        this.imageMapper = new BoursoramaImageMapper(codeToImageMap);
    }

    @Test
    public void shouldMapAllCharactersCorrectly() {
        assertThat(imageMapper.getPinCharacterRepresentation(1)).isEqualTo("abc");
        assertThat(imageMapper.getPinCharacterRepresentation(2)).isEqualTo("stv");
        assertThat(imageMapper.getPinCharacterRepresentation(3)).isEqualTo("def");
        assertThat(imageMapper.getPinCharacterRepresentation(4)).isEqualTo("qwe");
        assertThat(imageMapper.getPinCharacterRepresentation(5)).isEqualTo("mno");
        assertThat(imageMapper.getPinCharacterRepresentation(6)).isEqualTo("ghi");
        assertThat(imageMapper.getPinCharacterRepresentation(7)).isEqualTo("pqr");
        assertThat(imageMapper.getPinCharacterRepresentation(8)).isEqualTo("jkl");
        assertThat(imageMapper.getPinCharacterRepresentation(9)).isEqualTo("uvw");
        assertThat(imageMapper.getPinCharacterRepresentation(0)).isEqualTo("xyz");
    }
}
